import { Input } from '@mui/material';
import swal from '@sweetalert/with-react';


//este ejemplo es con sweetalert pero lo elimine :v asi que si se retoma hay que volver a instalar sweetalert.

export const ForgetPsswrd = () =>{
  
    swal({
        buttons: {
          codigo: "Codigo de recuperacion via email",
          preguntas:"Preguntas secretas",
          cancel:"cancel"
        },
        content: (
            <div>
            <h1>¿Has olvidado tu contraseña?</h1>
            <p>Selecciona una opción de recuperación</p>
        </div>
        )
    }).then((a)=>{
      var correo;
        switch (a) {
          case "codigo":
            swal(<div>
              <h1>Codigo de recuperacion</h1><p>Ingrese su correo para recibir un codigo de verificacion</p>
              {correo = <Input></Input>}
              {alert(correo)}
            </div>).then((correo)=>{
              swal(<div>
                <h1>Ingrese el codigo que se le proporcionó</h1>
                <Input></Input>
                </div>)
            })
            break;
          case "preguntas":
            swal("preguntas")
            break;
            case "cancel":
              swal("cancel")
              break;

          default:
            break;
        }
    })
    
    
}
